function fetchDataFromAPI() {
  // replace the URL below with the URL of the API you want to fetch data from
  var url = "https://api.example.com/data";

  // make a request to the API using the fetch() method
  var response = UrlFetchApp.fetch(url);

  // parse the response data as JSON
  var responseData = JSON.parse(response.getContentText());

  // get the active sheet in the current spreadsheet
  var sheet = SpreadsheetApp.getActiveSheet();

  // clear the existing data in the sheet
  sheet.clearContents();

  // set the headers for the data
  var headers = ["Column 1", "Column 2", "Column 3"];

  // insert the headers into the sheet
  sheet.appendRow(headers);

  // loop through the response data and insert each row into the sheet
  for (var i = 0; i < responseData.length; i++) {
    var row = [
      responseData[i].column1,
      responseData[i].column2,
      responseData[i].column3,
    ];
    sheet.appendRow(row);
  }
}
```
