    int s {0}, old_s {1};
    int t {1}, old_t {0};
    int r {b}, old_r {a};
    int quotient;

    while (r != 0) {
        quotient = old_r / r;
        old_r = std::exchange(r, old_r - quotient * r);
        old_s = std::exchange(s, old_s - quotient * s);
        old_t = std::exchange(t, old_t - quotient * t);
    }

    return {old_r, old_s, old_t};
}

int inverse(int n, int p) {
    auto [gcd, x, y] = euclidean_algo(n, p);
    if (gcd != 1) {
        return -1;
    } else {
        return x % p;
    }
}
std::pair<int, int> add_points(std::pair<int, int> p1, std::pair<int, int> p2, int p) {
    if (p1 == std::pair(0, 0)) {
        return p2;
    } else if (p2 == std::pair(0, 0)) {
        return p1;
    } else if (p1.first == p2.first && p1.second != p2.second) {
        return {0, 0};
    }

    int s, x, y;
    if (p1 == p2) {
        s = (3 * p1.first * p1.first + (A % p)) * inverse(2 * p1.second, p) % p;
    } else {
        s = (p1.second - p2.second) * inverse(p1.first - p2.first, p) % p;
    }
    x = ((s * s) - 2 * p1.first) % p;
    y = (p1.second + s * (x - p1.first)) % p;

    return {x, (-y + p) % p};
}

int order_point(std::pair<int, int> point, int p) {
    int i {1};
    auto check = add_points(point, point, p);
    while (check != std::pair(0, 0)) {
        check = add_points(check, point, p);
        i++;
    }
    return i;
}

bool is_prime_number(int number) {
    if (number < 2) {
        return false;
    }
    if (number == 2) {
        return true;
    }
    if (number % 2 == 0) {
        return false;
    }

    for (int i {3}; i <= std::sqrt(number); i += 2) {
        if (number % i == 0) {
            return false;
        }
    }

    return true;
}

int get_next_prime_number(int start) {
    int number {start};
    while (!is_prime_number(number)) {
        number++;
    }
    return number;
}

void find_opt_p(int p, int time_limit) {
    int last_p {p};
    int i {0};
    std::vector<std::pair<int, int>> points;
    std::default_random_engine generator;
    std::uniform_int_distribution distribution(0, p-1);

    while (true) {
        i++;
        std::cout << "Iteration " << i << ":" << std::endl;
        print_curve(p);

        time_t start = std::time(0);
        int cnt {0};

        for (int x {0}; x < p; ++x) {
            for (int y {0}; y < p; ++y) {
                cnt++;
                if (elliptic_curve(x, y, p)) {
                    points.push_back({x, y});
                }
            }

            if (cnt % 1000 == 0) {
                cnt = 0;
                if (difftime(std::time(0), start) > time_limit) {
                    break;
                }
            }
        }

        if (difftime(std::time(0), start) > time_limit) {
            std::cout << "------------Selection is over------------" << std::endl;
            std::cout << "[RESULT]: y^2 = x^3 + " << A % last_p << " * x + " << B % last_p << " [p = " << last_p << "]" << std::endl;
            break;
        }

        int cnt_points = points.size();
        std::cout << "Order curve = " << cnt_points << std::endl;
        auto point = points[distribution(generator)];
        std::cout << "Order point (" << point.first << ", " << point.second << "): " << order_point(point, p) << std::endl;
        std::cout << "Time: " << difftime(std::time(0), start) << " sec." << std::endl;
        std::cout << "-----------------------------------------" << std::endl;

        last_p = p;
        p = get_next_prime_number(p + 4500);
    }
}

int main() {
    int start_p {191};
    int time_limit {600};
    find_opt_p(start_p, time_limit);
    return 0;
}
