#include <iostream>
using namespace std;

// Function to check if the word is a palindrome using stack and array
bool isPalindrome(string word, int size) {
    // Create an array to act as a stack
    char stack[9];

    // Initialize the top of the stack
    int top = -1;

    // Push the first half of the word into the stack
    for (int i = 0; i < size / 2; i++) {
        top++;
        stack[top] = word[i];
    }

    // If the size is odd, skip the middle character
    int middle = (size % 2 == 0) ? size / 2 : (size / 2) + 1;

    // Pop the elements from the stack and compare with the second half of the word
    for (int i = middle; i < size; i++) {
        if (stack[top] != word[i]) {
            return false;
        }
        top--;
    }

    // If all the characters match, the word is a palindrome
    return true;
}

int main() {
    string word;
    int size;

    // Input the size of the palindrome
    cout << "Enter the size of the palindrome (between 0-9): ";
    cin >> size;

    // Check if the size is valid
    if(size < 0 || size > 9) {
        cout << "Invalid size! Please enter a value between 0-9.";
        return 0;
    }

    // Input the word
    cout << "Enter the word to be checked: ";
    cin >> word;

    // Check if the word is a palindrome and print the result
    if(isPalindrome(word, size)) {
        cout << "The word is a palindrome!";
    } else {
        cout << "The word is not a palindrome.";
    }

    return 0;
}
