    requestAnimationFrame(animate);

    uniforms.mouseCoord.value.x = currentX;
    uniforms.mouseCoord.value.y = -currentY + window.innerHeight;

    // Обновление положения и вращения объекта вокруг указанных координат
    rotatingCube.position.x = currentX + radius * Math.cos(time * rotationSpeed);
    rotatingCube.position.y = -currentY + window.innerHeight + radius * Math.sin(time * rotationSpeed);
